const WHITESPACE=/\s/,NUMBERS=/[0-9]/,LETTERS=/[a-z]/i,tokenizer=e=>{let t=0,s=[];for(;t<e.length;){let l=e[t];if("("!==l)if(")"!==l)if(WHITESPACE.test(l))t++;else if(NUMBERS.test(l)){let r="";for(;NUMBERS.test(l);)r+=l,l=e[++t];s.push({type:"number",value:r})}else if('"'!==l){if(!LETTERS.test(l))throw new TypeError("Unkown Token:"+l);{let r="";for(;LETTERS.test(l);)r+=l,l=e[++t];s.push({type:"name",value:r})}}else{let r="";for(l=e[++t];'"'!==l;)r+=l,l=e[++t];s.push({type:"string",value:r})}else s.push({type:"paren",value:")"}),t++;else s.push({type:"paren",value:"("}),t++}return s};module.exports=tokenizer;